mapper.select:
SELECT userId, userPwd
FROM member
WHERE userId = #{userId} AND userPwd = #{userPwd}

mapper.update:
UPDATE member
SET userPwd = #{userPwd},
    userName = #{userName}
WHERE userId = #{userId}

mapper.select:
SELECT COUNT(*)
FROM member
WHERE userId = #{userId}

mapper.delete:
DELETE FROM member
WHERE userId = #{userId}
  AND userPwd = #{userPwd}

mapper.select:
SELECT userId, userName, email, phone, dateOfBirth, reg_date
FROM member
WHERE userId = #{userId};
